import React, { useEffect, useState } from "react";
import { KeyboardAvoidingView } from "react-native";
import { Image } from "react-native";
import {
  StyleSheet,
  Text,
  TextInput,
  View,
  Button,
  FlatList,
  Pressable,
} from "react-native";
import DropDownPicker from "react-native-dropdown-picker";
import { useDispatch, useSelector } from "react-redux";
import { COLORS } from "../libs/constants";
import Icon from "react-native-vector-icons/Octicons";

const AddSingleIngredientScreen = () => {
  const singleIngredientChosen = useSelector(
    (state) => state.newIngredients.singleIngredient
  );

  const dispatch = useDispatch();

  const styles = StyleSheet.create({
    quantityContainer: {
      flex: 1,
      position: "absolute",
      height: "100%",
      width: "100%",
      backgroundColor: COLORS.white,
    },
    quantitySubtitle: {
      fontSize: 25,
      marginBottom: 10,
    },
    quantityInputContainer: {
      display: "flex",
      flexDirection: "row",
      marginBottom: 30,
      width: "100%",
      justifyContent: "space-evenly",
    },
    quantityInput: {
      borderBottomWidth: 2,
      borderBottomColor: COLORS.lightCoral,
      width: 50,
    },
    quantityNavigationBack: {
      flex: 3,
      justifyContent: "center",
      alignItems: "center",
      padding: 10,
      backgroundColor: COLORS.lightCoral,
      height: "100%",
    },
    quantityNavigationConfirm: {
      flex: 3,
      justifyContent: "center",
      alignItems: "center",
      padding: 10,
      backgroundColor: COLORS.grey,
      height: "100%",
    },
  });

  return (
    <>
      <KeyboardAvoidingView
        style={styles.quantityContainer}
        behavior="position"
      >
        {singleIngredientChosen ? (
          <>
            <Image
              source={{
                uri: singleIngredientChosen?.food?.image,
              }}
              style={{
                height: 150,
                width: 150,
                borderRadius: 10,
                marginTop: 60,
              }}
            />
            <Text
              style={{
                fontWeight: "800",
                fontSize: 35,
                marginTop: 10,
                textAlign: "center",
              }}
            >
              {singleIngredientChosen?.food?.label}
            </Text>
            <View
              style={{
                flex: 1,
                width: "100%",
                justifyContent: "center",
                alignItems: "center",
                marginTop: 30,
              }}
            >
              <Text style={styles.quantitySubtitle}>Quantity</Text>
              <View style={styles.quantityInputContainer}>
                <TextInput
                  keyboardType="numeric"
                  style={styles.quantityInput}
                  returnKeyType="done"
                  returnKeyLabel="Done"
                  onBlur={(text) =>
                    setSingleIngredientChosen({
                      ingredient: singleIngredientChosen.ingredient,
                      quantity: {
                        amount: text,
                        unit: "",
                      },
                    })
                  }
                />
                <DropDownPicker
                  items={[
                    {
                      label: "Unit",
                      value: "unit",
                      icon: () => <Icon name="flag" size={18} color="#900" />,
                    },
                    { label: "Weight", value: "weight", untouchable: true },
                    { label: "Grams", value: "grams", parent: "weight" },
                    { label: "Ounces", value: "ounces", parent: "weight" },
                    { label: "Cups", value: "cups", parent: "weight" },

                    {
                      label: "Volumes",
                      value: "volumes",
                      untouchable: true,
                    },
                    { label: "Oz", value: "teaspoon", parent: "volume" },
                    { label: "Milliliter", value: "mL", parent: "volume" },
                    { label: "Liter", value: "L", parent: "volume" },
                  ]}
                  defaultValue={"grams"}
                  containerStyle={{ height: 40, width: 100 }}
                  style={{ backgroundColor: COLORS.white, zIndex: 1000 }}
                  itemStyle={{ justifyContent: "flex-start" }}
                  onChangeItem={(item) =>
                    setSingleIngredientChosen({
                      ...singleIngredientChosen,
                      quantity: {
                        ...singleIngredientChosen.quantity,
                        unit: item.value,
                      },
                    })
                  }
                />
              </View>
            </View>
          </>
        ) : (
          <Text>Loading</Text>
        )}
      </KeyboardAvoidingView>
    </>
  );
};

export default AddSingleIngredientScreen;
