import { receiveIngredientsSearch } from "../redux/actions/IngredientsSearchActions";

const fetchIngredientsSearchApi = (dispatch, text) => {
  fetch(`http://localhost:4000/api/edaman/ingredients?type=ingr&search=${text}`)
    .then((res) => res.json())
    .then((json) => {
      console.log(json);
      dispatch(receiveIngredientsSearch({ ingredients: json.data.hints }));
    })
    .catch((err) => console.log(err.message));
};

export default fetchIngredientsSearchApi;
